const today = new Date().toISOString().split('T')[0];

// M√©tricas principais
const metrics = {
  date: today,
  totalMessages: data.totalMessages || 0,
  totalLeads: data.totalLeads || 0,
  totalConversions: data.totalConversions || 0,
  averageResponseTime: data.averageResponseTime || 0,
  topIntentions: data.topIntentions || [],
  hourlyDistribution: data.hourlyDistribution || [],
  conversionRate: data.totalLeads > 0 ? (data.totalConversions / data.totalLeads * 100).toFixed(2) : 0,
  responseRate: data.totalMessages > 0 ? (data.totalResponses / data.totalMessages * 100).toFixed(2) : 0
};

// Calcular tend√™ncias
const yesterday = data.yesterday || {};
const trends = {
  messagesGrowth: yesterday.totalMessages ? 
    ((metrics.totalMessages - yesterday.totalMessages) / yesterday.totalMessages * 100).toFixed(2) : 0,
  leadsGrowth: yesterday.totalLeads ? 
    ((metrics.totalLeads - yesterday.totalLeads) / yesterday.totalLeads * 100).toFixed(2) : 0,
  conversionsGrowth: yesterday.totalConversions ? 
    ((metrics.totalConversions - yesterday.totalConversions) / yesterday.totalConversions * 100).toFixed(2) : 0
};

// Alertas autom√°ticos
const alerts = [];
if (metrics.conversionRate < 10) {
  alerts.push({
    type: 'warning',
    message: `Taxa de convers√£o baixa: ${metrics.conversionRate}%`,
    action: 'Revisar estrat√©gia de follow-up'
  });
}

if (metrics.averageResponseTime > 60) {
  alerts.push({
    type: 'critical',
    message: `Tempo de resposta alto: ${metrics.averageResponseTime}s`,
    action: 'Verificar performance do sistema'
  });
}

if (trends.leadsGrowth < -20) {
  alerts.push({
    type: 'warning',
    message: `Queda de leads: ${trends.leadsGrowth}%`,
    action: 'Analisar campanhas de marketing'
  });
}

return [{
  json: {
    metrics: metrics,
    trends: trends,
    alerts: alerts,
    reportGenerated: true,
    timestamp: new Date().toISOString()
  }
}];

